import React from 'react';

export const Transaction= ({transaction}) => {
  const sign = transaction.amount < 0 ? '-' : '+';
  let date= new Date.prototype.toDateString()
  return <li className="minus">
      {date} <span>{transaction.text} </span> <span>{sign}${Math.abs(transaction.amount)}</span><button className="delete-btn">x</button>
        </li> ;
};
