import React, { useContext } from 'react';
import { GlobalContext } from '../context/GlobalState';

export const Transaction= ({transaction}) => {
  const {deleteTransaction}=useContext(GlobalContext)
  const sign = transaction.amount < 0 ? '-' : '+';
  let date= new Date()
  let d=date.toLocaleDateString()
  return <li className={transaction.amount<0 ? 'minus': 'plus'}>
      {d}<span>{transaction.text}</span> <span>{sign}Ksh.{Math.abs(transaction.amount)}</span><button onClick={() => deleteTransaction(transaction.id)} className="delete-btn">x</button>
        </li> ;
};
